mapper.update:
UPDATE Board
SET view_count = view_count + 1
WHERE board_id = #{boardId}

UPDATE board
  SET 
    title = #{board.title},
    contents = #{board.contents},
    board_category_id = #{board.boardCategoryId},
    udt_dt = #{board.udtDt}
  WHERE board_id = #{board.boardId}
  AND member_id = #{board.memberId}
