mapper.select:
SELECT COUNT(*)
FROM board
WHERE member_id = #{memberId}

SELECT COUNT(*)
FROM recipe_comment
WHERE member_id = #{memberId}

SELECT COUNT(*)
FROM recipe_like
WHERE member_id = #{memberId}

SELECT *
FROM board
WHERE member_id = #{memberId}
ORDER BY reg_dt DESC
LIMIT #{display}
OFFSET #{start}

SELECT *
FROM recipe_comment
WHERE member_id = #{memberId}
ORDER BY reg_dt DESC
LIMIT #{display}
OFFSET #{start}

SELECT rl.member_id, rl.recipe_id, rl.reg_dt, mr.title
FROM recipe_like rl
JOIN member_recipe mr ON rl.recipe_id = mr.recipe_id
WHERE rl.member_id = #{memberId}
ORDER BY rl.reg_dt DESC
    LIMIT #{display}
    OFFSET #{start}

SELECT rc.recipe_id , rc.comment_id , rc.member_id , rc.comment , rc.rate , rc.reg_dt , rc.upt_dt , rc.status , rc.p_comment_id , rc.del_yn , mr.title FROM recipe_comment rc LEFT JOIN member_recipe mr ON rc.recipe_id = mr.recipe_id WHERE rc.member_id = ${memberId} and del_yn = 'N' ORDER BY
rc.recipe_id ${sortDirection}
rc.reg_dt ${sortDirection}
rc.comment_id ${sortDirection}
LIMIT #{pageSize} OFFSET GREATEST(0, #{offset})

mapper.update:
UPDATE recipe_comment
SET del_yn = 'Y', upt_dt = NOW()
WHERE comment_id = #{commentId} AND member_id = #{memberId};

mapper.select:
SELECT recipe_id, member_id, recipe_category_id, title, "method", img_id, serving, "level", "time", calory, tip, reg_dt, upt_dt, view_count, comment_rate
FROM member_recipe
WHERE member_id = #{memberId}
ORDER BY reg_dt DESC
LIMIT #{limit} OFFSET #{start}
