mapper.select:
SELECT COUNT(*)
FROM recipe_like
WHERE member_id = #{memberId}

SELECT COUNT(*)
FROM board
WHERE member_id = #{memberId}

SELECT *
FROM board
WHERE member_id = #{memberId}
ORDER BY reg_dt DESC
LIMIT #{display}
OFFSET #{start}

SELECT *
FROM recipe_comment
WHERE member_id = #{memberId}
ORDER BY reg_dt DESC
LIMIT #{display}
OFFSET #{start}

SELECT rl.member_id, rl.recipe_id, rl.reg_dt, mr.title
FROM recipe_like rl
JOIN member_recipe mr ON rl.recipe_id = mr.recipe_id
WHERE rl.member_id = #{memberId}
ORDER BY rl.reg_dt DESC
LIMIT #{display}
OFFSET #{start}

SELECT a.recipe_id, -- 레시피 ID a.member_id, -- 작성자 ID a.title, -- 레시피 제목 a.view_count, -- 조회수 a.reg_dt, -- 등록 날짜 a.upt_dt, -- 수정 날짜 (SELECT COUNT(*) FROM recipe_comment x WHERE x.recipe_id = a.recipe_id AND x.del_yn = 'N') as comment_count, -- 댓글 수 a.comment_rate, -- 댓글 평점 (SELECT COUNT(*) FROM recipe_like x WHERE x.recipe_id = a.recipe_id) as like_count, -- 좋아요 수 d.server_img_name, -- 서버에 저장된 이미지 이름 d.extension, -- 이미지 확장자 d.web_img_path, -- 웹 접근 경로 c.user_id, -- 작성자 사용자 ID c.name, -- 작성자 이름 c.phone, -- 작성자 전화번호 c.role, -- 작성자 역할 c.email -- 작성자 이메일 FROM recipe_like b JOIN member_recipe a ON b.recipe_id = a.recipe_id JOIN member c ON a.member_id = c.member_id LEFT JOIN member_recipe_images d ON a.img_id = d.img_id WHERE b.member_id = #{memberId} -- 찜한 사용자 ID
AND a.recipe_category_id = #{recipeCategoryId}
ORDER BY ${orderby} DESC
ORDER BY a.reg_dt DESC
LIMIT #{display} OFFSET #{start}

SELECT rc.recipe_id, rc.comment_id, rc.member_id, rc.comment, rc.rate, rc.reg_dt, rc.upt_dt, rc.status, rc.p_comment_id, rc.del_yn, mr.title FROM recipe_comment rc LEFT JOIN member_recipe mr ON rc.recipe_id = mr.recipe_id WHERE rc.member_id = #{memberId} AND rc.del_yn = 'N' ORDER BY
rc.recipe_id ${sortDirection}
rc.reg_dt ${sortDirection}
rc.comment_id ${sortDirection}
LIMIT #{pageSize} OFFSET GREATEST(0, #{offset})

mapper.update:
UPDATE recipe_comment
SET del_yn = 'Y', upt_dt = NOW()
WHERE comment_id = #{commentId} AND member_id = #{memberId};
